import React, { useState } from 'react';
import Navbar from './components/Navbar';
import ThemeToggle from './components/ThemeToggle';
import AddBook from './components/Admin/AddBook';
import BookList from './components/Admin/BookList';
import Cart from './components/Customer/Cart';

const App = () => {
  const [activePage, setActivePage] = useState('addBook');

  const renderPage = () => {
    if (activePage === 'addBook') return <AddBook />;
    if (activePage === 'bookList') return <BookList />;
    if (activePage === 'cart') return <Cart cartItems={[]} onCheckout={() => alert('Checkout Successful!')} />;
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200">
      {/* Navbar */}
      <ThemeToggle />
      <Navbar activePage={activePage} setActivePage={setActivePage} />

      {/* Main Content */}
      <main className="container mx-auto mt-8 p-4">
        {renderPage()}
        <div className="flex justify-end">

        </div>
      </main>
    </div>
  );
};

export default App;
