import React, { useState } from 'react';
import ThemeToggle from './components/ThemeToggle';
import BookList from './components/Admin/BookList';
import AddBook from './components/Admin/AddBook';
import EditBook from './components/Admin/EditBook';
import SearchBooks from './components/Customer/SearchBooks';
import BookDetails from './components/Customer/BookDetails';
import Cart from './components/Customer/Cart';

const App = () => {
  const [editingBook, setEditingBook] = useState(null); // For editing books
  const [cartItems, setCartItems] = useState([]); // For customer cart
  const [selectedBook, setSelectedBook] = useState(null); // For book details display

  const handleEdit = (book) => {
    setEditingBook(book);
  };

  const handleSave = () => {
    setEditingBook(null); // Close edit mode after saving
  };

  const handleSearch = (query) => {
    // Example search logic (you can integrate with your backend API)
    console.log(`Searching for: ${query}`);
  };

  const handleAddToCart = (book) => {
    const existingItem = cartItems.find((item) => item.id === book._id);
    if (existingItem) {
      if (existingItem.quantity < 5) {
        setCartItems(
          cartItems.map((item) =>
            item.id === book._id ? { ...item, quantity: item.quantity + 1 } : item
          )
        );
      } else {
        alert('You cannot add more than 5 copies of this book.');
      }
    } else {
      setCartItems([...cartItems, { id: book._id, title: book.title, quantity: 1 }]);
    }
  };

  const handleCheckout = () => {
    // Example checkout logic
    alert('Checkout successful!');
    setCartItems([]);
  };

  return (
    <div className="container mx-auto p-4">
      {/* Theme Toggle */}
      <ThemeToggle />

      {/* Admin Section */}
      <h1 className="text-2xl font-bold my-4">Admin Panel</h1>
      <AddBook />
      {editingBook ? (
        <EditBook book={editingBook} onSave={handleSave} />
      ) : (
        <BookList onEdit={handleEdit} />
      )}

      {/* Customer Section */}
      <h1 className="text-2xl font-bold my-4">Customer Interface</h1>
      <SearchBooks onSearch={handleSearch} />
      {selectedBook ? (
        <BookDetails book={selectedBook} />
      ) : (
        <p>Select a book to see its details</p>
      )}
      <Cart cartItems={cartItems} onCheckout={handleCheckout} />
    </div>
  );
};

export default App;
