import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  // Fetch books when the component mounts
  useEffect(() => {
    axios
      .get('http://localhost:5000/api/books') // Ensure your backend is running on port 5000
      .then((response) => {
        setBooks(response.data); // Set the fetched books to state
      })
      .catch((error) => {
        console.error('Error fetching books:', error); // Log any errors
        setError(error.response?.data?.message || 'Failed to fetch books');
      });
  }, []);

  return (
    <div>
      <h2>Book List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {books.length > 0 ? (
        <ul>
          {books.map((book) => (
            <li key={book._id}>
              {book.title} - {book.author} (${book.price})
            </li>
          ))}
        </ul>
      ) : (
        <p>No books found.</p>
      )}
    </div>
  );
};

export default BookList;
