import React from 'react';
import ThemeToggle from './components/ThemeToggle';
import BookList from './components/Admin/BookList';
import AddBook from './components/Admin/AddBook';

const App = () => {
  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200">
      <header className="bg-blue-600 dark:bg-blue-800 text-white py-4 text-center">
        <h1 className="text-3xl font-bold">Bookstore Management</h1>
        <ThemeToggle />
      </header>
      <main className="container mx-auto mt-8 p-4">
        <AddBook />
        <BookList />
      </main>
    </div>
  );
};

export default App;
